cool_oneliner = [cislo := [], [(prevod := lambda c, r: [cislo.append(c%r if c%r <= 9 else chr(87+c%r)), (prevod(c//r, r) if c//r != 0 else cislo.append((orig, r)))])(c, r) for [((c, orig),), r] in [[[(i, i) for i in (int(input('cislo v desiatkovej sustave: ')),)], int(input('radix v desiatkovej sustave: '))]]], print(f'cislo {cislo[-1][0]} v {cislo[-1][1]}-kovej sustave je:', ''.join(map(str, reversed(cislo[:-1]))))]